import React from 'react'
import SwipableCard from './SwipableCard'
import Card from './Card'
import { getConceptExplanations } from '../utils/firebase'
import { withRouter } from 'next/router'

class SwipableCardContainer extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      explanationCardDeck: this.props.conceptExplanationsArray,
      displayCardIndex: 0
    }
  }
  componentDidMount() {

  }
  handleSwipe = () => {
    this.setState({
      displayCardIndex: this.state.displayCardIndex + 1
    })
  }
  handleReplay = () => {
    this.setState({
      displayCardIndex: 0
    })
  }
  render() {
    if (this.state.displayCardIndex == this.state.explanationCardDeck.length ) {
      // no more cards to display
      return (
        <Card>
          <p>No more cards to display.</p>
          <div onClick={this.handleReplay} className="button">Replay</div>
        </Card>
      )
    } else {
      return (
        <Card>
          <p>{`this.state.explanationCardDeck.length: ${this.state.explanationCardDeck.length}`}</p>
          <p>{`this.state.displayCardIndex: ${this.state.displayCardIndex}`}</p>
          <p onClick={this.handleSwipe}>{JSON.stringify(this.state.explanationCardDeck[this.state.displayCardIndex])}</p>
        </Card>
      )
    }
  }
}

export default SwipableCardContainer
