import React from 'react'
import Header from '../components/Header'
import AppContainer from '../components/AppContainer'
import Leaderboard from '../components/Leaderboard'
import { getTopCreatorsAllTime } from '../components/firebase'

const LeaderboardPage = props => {
  return (
    <div>
      <Header loggedIn={props.loggedIn}/>
      <AppContainer>
        <h1 className="font-lrg font-bold-med">The most ðŸ”¥ Aha Moment creators</h1>
        <Leaderboard />
      </AppContainer>
    </div>
  )
}

LeaderboardPage.getInitialProps = async function() {
  let data = await getTopCreatorsAllTime()

  return {
    shows: data.map(entry => entry.creator)
  };
};

export default LeaderboardPage
