import { withRouter } from 'next/router'

class SwipableCardContainer extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      explanationCardDeck: this.props.conceptExplanationsArray,
      displayCardIndex: 0
    }
  }
  componentDidMount() {

  }
  handleSwipe = () => {
    this.setState({
      displayCardIndex: this.state.displayCardIndex + 1
    })
  }
  handleReplay = () => {
    this.setState({
      displayCardIndex: 0
    })
  }
  render() {
