    //     </Card>
    //   )
    // } else {
      return (
        <Card onClick={this.handleSwipe}>
          <p>{JSON.stringify(this.state.explanationCardDeck[this.state.displayCardIndex])}</p>
        </Card>
      )
    // }
  }
}

export default SwipableCardContainer
