import React from 'react'
import Card from './Card'
import CardSection from './CardSection'
import UploadImage from './uploadExplanationComponents/UploadImage'
import { saveExplanationWithFileToDB } from '../utils/firebase'
import { withRouter } from 'next/router'

class AddExplanationCard extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      type: '', // photo, video, audio, podcast, youtube, tweet, link, text
      fileToUpload: '',
      introText: '',
      mediaLink: '',
      mediaConsumptionGuidance: '',
      showAddExplanationSection: false,
      typeSelected: false,
      activeElement: "none" // write, podcast, youtube, recordVideo, recordAudio, tweet, uploadVideo, uploadPhoto, draw, link
    }
  }
  handleFileChange = (e) => {
    this.setState({fileToUpload: e.target.files[0]});
  }
  handleIntroTextChange = (e) => {
    this.setState({introText: e.target.value});
  }
  handleSubmit = (e) => {
    const { router } = this.props
    e.preventDefault()
    saveExplanationWithFileToDB(this.state.introText, this.state.fileToUpload, this.state.type, this.props.loggedInUser.userID, router.query.id)
  }
  render() {
    console.log(this.state.activeElement)
    const { router } = this.props
    return (
      <div>
        <Card>
          <CardSection>
            <div className="columns-parent-div">
              <div className="column-80-p">
                <p className="font-color-light-grey">Add your own explanation and give someone an aha moment.</p>
              </div>
              <div className="column-20-p">
                {
                  !this.state.showAddExplanationSection &&
                  <div onClick={() => { this.setState({ showAddExplanationSection: true }) }} className="action-button button background-purple rounded-border font-med font-bold-extra float-right">Contribute</div>
                }
                {
                  this.state.showAddExplanationSection &&
                  <div onClick={() => { this.setState({ showAddExplanationSection: false, typeSelected: false }) }} className="font-med font-bold-extra float-right"><i className="material-icons">close_rounded</i></div>
                }
              </div>
            </div>
          </CardSection>
          {
            this.state.showAddExplanationSection &&
            <CardSection>
              <h2 className="font-med">
                <span className="link">@barackobama </span>
                explains
                <span className="link"> {router.query.id} </span>
                through
                <span className="link"> spoken word</span>
              </h2>
              <div className="media-type-selection-section margin-top-sml">
                <div onClick={() => { this.setState({ typeSelected: "text", activeElement: "write" }) }} className={this.state.activeElement == "write" ? "background-purple action-button button rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml" : "background-dark-grey action-button button rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml"}>Write Something</div>
                <div onClick={() => { this.setState({ typeSelected: "podcast", activeElement: "podcast" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Podcast Snippet</div>
                <div onClick={() => { this.setState({ typeSelected: "youtube", activeElement: "youtube" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">YouTube Clip</div>
                <div onClick={() => { this.setState({ typeSelected: "video", activeElement: "recordVideo" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Record Your Own Video</div>
                <div onClick={() => { this.setState({ typeSelected: "audio", activeElement: "recordAudio" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Record Your Own Audio</div>
                <div onClick={() => { this.setState({ typeSelected: "tweet", activeElement: "tweet" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Tweet</div>
                <div onClick={() => { this.setState({ typeSelected: "video", activeElement: "uploadVideo" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Upload A Video</div>
                <div onClick={() => { this.setState({ typeSelected: "photo", activeElement: "uploadPhoto" }) }} className="action-button button background-purple rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Upload A Photo</div>
                <div onClick={() => { this.setState({ typeSelected: "photo", activeElement: "draw" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Draw Something And Upload It</div>
                <div onClick={() => { this.setState({ typeSelected: "link", activeElement: "link" }) }} className="action-button button background-dark-grey rounded-border font-med font-bold-med margin-right-sml margin-bottom-sml">Link To Something On The Web</div>
              </div>
            </CardSection>
          }
          {
            this.state.showAddExplanationSection && this.state.typeSelected &&
            <div>
              <CardSection>
                <UploadImage handleIntroTextChange={this.handleIntroTextChange} handleFileChange={this.handleFileChange} currentConcept={router.query.id} />
              </CardSection>
              <CardSection>
                <div onClick={this.handleSubmit} className="action-button button background-purple rounded-border font-med font-bold-med">Submit!</div>
              </CardSection>
            </div>
          }
        </Card>
      </div>
    )
  }
}

export default withRouter(AddExplanationCard)


