import React from 'react'
import Loading from '../Loading'
import { getFileURLFromFirebaseStorage } from '../../utils/firebase'

class EmbeddedTweet extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      tweetUrl: false
    }
  }
  async componentDidMount() {
    this.setState({
      tweetUrl: await getFileURLFromFirebaseStorage(this.props.tweet)
    })
  }
  render() {
    if (!this.state.tweetUrl) {
      return <Loading />
    }
    return (
      <div className="center">
        <img className="inline-explanation-image" src={this.state.tweetUrl} />
      </div>
    )
  }
}

export default EmbeddedTweet
