import React from 'react'
import Card from '../Card'
import CardSection from '../CardSection'

class WriteText extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      fileToUpload: ''
    }
  }
  sendTextChangeToParent = (e) => {
    this.props.handleTextChange(e)
  }
  render() {
    return (
      <div>
        <form className="add-explanation-form">
          <h2 className="font-med font-bold-med">Go for it! Teach us.</h2>
          <textarea onChange={this.sendTextChangeToParent} className="margin-top-sml font-med background-grey rounded-border light-border" type="text" name="text" placeholder={`Here is a fabulous way to understand ${this.props.currentConcept}s...`} rows="8"></textarea>
        </form>
      </div>
    )
  }
}

export default WriteText

