    // } else {
      return (
        <Card>
          <p>{`this.state.explanationCardDeck.length: ${this.state.explanationCardDeck.length}`}</p>
          <p>{`this.state.displayCardIndex: ${this.state.displayCardIndex}`}</p>
          <p onClick={this.handleSwipe}>{JSON.stringify(this.state.explanationCardDeck[this.state.displayCardIndex])}</p>
        </Card>
      )
    // }
  }
}

export default SwipableCardContainer
