import React from 'react'
import ExplanationCard from './ExplanationCard'
import Zoom from 'react-reveal/Zoom';

class SwipableCard extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      show: true
    }
  }
  handleGoToNextCard = () => {
    this.setState({ show: false })
  }
  render() {
    return (
      <div onClick={this.handleGoToNextCard}>
        <Zoom left opposite when={this.state.show}>
          <ExplanationCard key={this.props.explanationID} explanation={this.props.explanation} loggedInUser={this.props.loggedInUser} />
        </Zoom>
      </div>
    )
  }
}

export default SwipableCard
